import { Component } from 'react';
import {
    Card, CardText, CardBody, CardTitle, CardSubtitle, Button,
    Modal,
    ModalHeader,
    ModalBody

} from 'reactstrap';

import { addToCart } from '../actions/cartActions';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { clearErrors } from '../actions/errorActions';

class ItemModal extends Component {
    state = {
        modal: false,

    };


   static propTypes = {

        item: PropTypes.object.isRequired,
        isAuthenticated: PropTypes.bool,
        addToCart: PropTypes.func.isRequired,
        user: PropTypes.object.isRequired
    }

    onAddToCart = async (id, productId) => {
        await this.props.addToCart(id, productId, 1);
        alert ('Item added to Cart');
    }

    toggle = () => {
        // Clear errors
       // this.props.clearErrors();
        this.setState({
            modal: !this.state.modal
        });
    }



    render(){
        const  item  = this.props.item;

        return(
            <div className="container">
        <a className="my-1 text-sm font-medium text-gray-700 dark:text-gray-200 hover:text-blue-500 dark:hover:text-blue-400 lg:mx-4 lg:my-0" onClick={this.toggle}><CardTitle tag="h5">{item.title}</CardTitle></a>
        <h2 classNAme="text-gray-900 title-font text-lg font-medium" onClick={this.toggle}>{item.title}</h2>
         <Modal
                    isOpen={this.state.modal}
                    toggle={this.toggle}
                >
                    <ModalHeader toggle={this.toggle}>
                        Product
                    </ModalHeader>
                    <ModalBody>
                    <Card className="mb-4">
                        <CardBody>
                            <CardTitle tag="h5">{item.title}</CardTitle>
                            <CardSubtitle tag="h6">Rs. {item.price}</CardSubtitle>


                       <CardText>{item.category}</CardText>

                            {this.props.isAuthenticated ? 
                                <Button
                                    color="success"
                                    size="sm"
                                    onClick={this.onAddToCart.bind(this, this.user._id, this.item._id)}
                                    >Add To Cart</Button> :
                                    null}
                        </CardBody>
                    </Card>

                    </ModalBody>
                </Modal>

           </div>
        );
    }
}

export default ItemModal;
