import { Component } from 'react';
import {
    Card, CardText, CardBody, CardTitle, CardSubtitle, Button,
    Modal,
    ModalHeader,
    ModalBody

} from 'reactstrap';

import { addToCart } from '../actions/cartActions';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { clearErrors } from '../../actions/errorActions';

class ItemModal extends Component {
    state = {
        modal: false,

    };



    static propTypes = {

        item: PropTypes.object.isRequired,
        isAuthenticated: PropTypes.bool,
        addToCart: PropTypes.func.isRequired,
        user: PropTypes.object.isRequired
    }

    onAddToCart = async (id, productId) => {
        await this.props.addToCart(id, productId, 1);
        alert ('Item added to Cart');
    }

    toggle = () => {
        // Clear errors
        this.props.clearErrors();
        this.setState({
            modal: !this.state.modal
        });
    }



    render(){
        return(
            <div className="container">
            {  /*  <Button color="success" className="btn btn-sm"><NavLink onClick={this.toggle} href="#"><span className="text-dark"><b>Login</b></span></NavLink></Button>
   */ }
               <a className="my-1 text-sm font-medium text-gray-700 dark:text-gray-200 hover:text-blue-500 dark:hover:text-blue-400 lg:mx-4 lg:my-0" onClick={this.toggle} href="#">Login</a>

                <Modal
                    isOpen={this.state.modal}
                    toggle={this.toggle}
                >
                    <ModalHeader toggle={this.toggle}>
                        <CardTitle tag="h5">{this.item.title}</CardTitle>
                    </ModalHeader>
                    <ModalBody>
                    <Card className="mb-4">
                        <CardBody>

                            <CardSubtitle tag="h6">Rs. {this.item.price}</CardSubtitle>


                        <CardText>{item.category}</CardText>
                            {this.props.isAuthenticated ? 
                                <Button
                                    color="success"
                                    size="sm"
                                    onClick={this.onAddToCart.bind(this, this.user._id, item._id)}
                                    >Add To Cart</Button> :
                                    null}
                        </CardBody>
                    </Card>

                    </ModalBody>
                </Modal>
            </div>
        );
    }
}

const mapStateToProps = (state) => ({
    item: state.item,
    isAuthenticated: state.auth.isAuthenticated,
    error: state.error
});

export default connect(mapStateToProps, {addToCart})(ItemModal);
