import { Component } from 'react';
import {
    Button,
    Modal,
    ModalHeader,
    ModalBody

} from 'reactstrap';
class SearchModal extends Component {
    state = {
        modal: false,

    };
    toggle = () => {
        // Clear errors
       // this.props.clearErrors();
        this.setState({
            modal: !this.state.modal
        });
    }

    render(){
    return (
<div class=" bg-gray-200">

        <Modal
        isOpen={this.state.modal}
        toggle={this.toggle}
    >
        <ModalHeader toggle={this.toggle}>
            {item.title}
        </ModalHeader>
        <ModalBody>
        <div className="">
<a className="block relative h-48 rounded overflow-hidden">
  <img className="h-full lg:max-w-2xl" src={item.image_1} alt="Catalogue-pana.svg" />
</a>
<div className="mt-4 items-center">
  <h3 className="text-gray-500 text-xs tracking-widest title-font mb-1">CATEGORY</h3>

  <span className="fa fa-star checked"></span>
  <span className="fa fa-star checked"></span>
  <span className="fa fa-star checked"></span>
  <span className="fa fa-star"></span>
  <span className="fa fa-star"></span>
  <div className="flex items-center justify-between  "> 
  <div>
  <p className="mt-1">{item.sell_price}€</p>

           </div>

  {this.props.isAuthenticated ? 
                    <Button
                        color="success"
                        size="sm"
                        onClick={this.onAddToCart.bind(this, user._id, item._id)}
                        >Add To Cart</Button> :
                        null}
  </div> 

  <div>
      <p>{item.description}</p>
  </div>
    </div> 
  </div> 

        </ModalBody>
    </Modal>

    <div class="container h-screen flex justify-center items-center px-4 sm:px-6 lg:px-8">
        <div class="relative"> 
        <input type="text" class="h-14 w-96 pr-8 pl-5 rounded z-0 focus:shadow focus:outline-none" placeholder="Search anything..."/>
            <div class="absolute top-4 right-3"> <i class="fa fa-search text-gray-400 z-20 hover:text-gray-500"></i> </div>
        </div>
    </div>
</div>
    );}
};

export default SearchModal;
